class NewsMake:
    user_news = {}
    channel = '@test_channel_bota'
    def send_news(self,news_text):
        btn_approve = types.InlineKeyboardButton(text='–û–ø—É–±–ª–∏–∫–æ–≤–∞—Ç—å', callback_data ="publish")
        btn_cancel = types.InlineKeyboardButton(text='–û—Ç–º–µ–Ω–∞', callback_data ="—Åancel")
        markup =types.InlineKeyboardMarkup()
        markup.row(btn_approve,btn_cancel)
        msg =bot.send_message(chat_id,news_text,parse_mode='HTML',reply_markup = markup)
        self.user_news[msg.message_id] = news_text



        @staticmethod
        @bot.callback_query_handler(func=lambda call: True)
        def approve(call):
            message_id = call.message.message_id
            news_text = self.user_news.get(message_id)
            if call.data == 'publish':
                bot.send_message(self.channel, news_text, parse_mode='HTML', )
            elif call.data == 'cancel':
                bot.send_message(channel, news_text, parse_mode='HTML', )

